import * as React from 'react';

import styled from 'styled-components';

const StyledItem = styled.li`
  display: flex;
  align-items: center;
  padding-bottom: 5px;
`;

const StyledColumn = styled.span`
  padding: 0 5px;
  white-space: nowrap;
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
  a {
    color:inherit;
  }
  width: ${(props) => props.width};
`;

const StyledButton = styled.button`
  background: transparent;
  border: 1px solid #171212;
  padding: 5px;
  cursor: pointer;
  transition: all 0.1s ease-in;
  &:hover > svg > g {
    background: #171212;
    color: #ffffff;
  }
`;

const StyledButtonSmall = styled(StyledButton)`
  padding: 5px;
`;

type SortAction = string;

const storiesReducer = (
  action: SortAction
) => {
  switch (action.type) {
    case 'SORT_TOPIC':
      sort(action.payload)
    case 'SORT_UPVOTES':
      sort(action.payload)
    case 'SORT_COMMENTS':
      sort(action.payload) 
    default:
      throw new Error();
  }
};

type Story = {
  id: string;
  url: string;
  title: string;
  by: string;
  descendants: number;
  score: number;
};

type Stories = Array<Story>;

type ListProps = {
  list: Stories;
  onSortList: string;
  onRemoveItem: (item: Story) => void;
};

const List = ({ list, onSortList, onRemoveItem }: ListProps) => {
  const [sort, dispatchSort] = React.useReducer(
    sortReducer,
    { }
  );

  return(
    <React.Fragment>
      <StyledButtonSmall
        type="button" 
        onClick={() => onSortList(topic)}
      >
        Topic
      </StyledButtonSmall>

      <StyledButtonSmall
        type="button" 
        onClick={() => onSortList(upvotes)}
      >
        Upvotes
      </StyledButtonSmall>

      <StyledButtonSmall
        type="button" 
        onClick={() => onSortList(comments)}
      >
        Comments
      </StyledButtonSmall>

      <ul>
        {console.log(list)}
        {list.map((item) => (
          <Item
            key={item.id}
            item={item}
            onRemoveItem={onRemoveItem}
          />
        ))}
        {console.log(list)}
      </ul>
    </React.Fragment>
   );
};

type ItemProps = {
  item: Story;
  onRemoveItem: (item: Story) => void;
};

const Item = ({ item, onRemoveItem }: ItemProps) => (
  <StyledItem>
    <StyledColumn width="40%">
      <a href={item.url}>{item.title}</a>
    </StyledColumn>
    <StyledColumn width="30%">{item.by}</StyledColumn>
    <StyledColumn width="10%">{item.descendants}</StyledColumn>
    <StyledColumn width="10%">{item.score}</StyledColumn>
    <StyledColumn width="10%">
    <StyledButtonSmall
      type="button" 
      onClick={() => onRemoveItem(item)}
    >
      Dismiss
    </StyledButtonSmall>
    </StyledColumn>
  </StyledItem>
);

export { List, Item };
